document.querySelectorAll('[data-slider="wrapper"]').forEach(e=>{let l=e.querySelectorAll(".w-dyn-item"),r=e.querySelector(".w-slider-mask"),t=r.querySelector(".w-slide");if(0==l.length){let d=e.querySelector(".w-dyn-empty");e.appendChild(d),r.parentElement.remove()}else l.forEach(e=>{let l=t.cloneNode(!0);r.appendChild(l),l.appendChild(e.children[0])}),t.remove(),r.scrollWidth<=r.clientWidth&&(e.querySelector(".w-slider-arrow-left").style.display="none",e.querySelector(".w-slider-arrow-right").style.display="none",e.querySelector(".w-slider-nav").style.display="none");e.querySelectorAll(".w-dyn-list").forEach(e=>{e.remove()})});